import { useRouter } from 'next/router';
import Layout from '../../components/Layout';

const Post = () => {
  const router = useRouter();

  return (
    <Layout>
      <h1>{router.query.author}</h1>
  <p>{router.query.quote}</p>
    </Layout>
  );
};

export default Post;
